import {useState} from 'react';



const BcOpt = (props) => {
  const [selectedOption, setSelectedOption] = useState("");
const [selectedOptionIsValid, setSelectedOptionIsValid] = useState(false);
  const handleChange = (event) => {
    let option = event.target.value;

    setSelectedOption(option);
    props.update("bcOpt", {selectedOption: option});
  };

 const disableButton =(event) =>{
  if (event.target.checked === false){
    return true;
    };
    setSelectedOptionIsValid(true);
 }
const onClickHandler = (event) =>{
  if(event.target.checked === false){
    alert('You must make a selection to continue');
  };
  return props.nextPage;
}
  return (
    <div class='container'>
      <h1>Choose Your Template</h1>
<div class='row'>
    <div class ='col'>

        <input
          type="radio"
          name='bcOpt'
          value="Classic Version"
          checked={selectedOption === "Classic Version"}
          onChange={handleChange}
         />
        <img src="/Assets/WashU/BCOptions.jpg" class ='img-fluid' alt =''/>
      </div><div class ='col'>



        <input
          type="radio"
          name='bcOpt'
          value="Updated Version"
          checked={selectedOption === "Updated Version"}
          onChange={handleChange}
        />
        <img src="/Assets/WashU/BCOptions2.jpg"  class='img-fluid' alt =''/>
        {!selectedOptionIsValid && <p>You must select one of the above options to continue</p>}
        <button onClick={onClickHandler} disable={disableButton}>Next</button>
        </div>
</div>


    </div>
  );
};
export default BcOpt;
