// import {useState} from 'react';
import classes from "./BcOpt.module.css";
const BcOpt = (props) => {
  const [selectedOption, setSelectedOption] = useState("");
  const handleChange = (event) => {
    event.prevent.default();
    let option = event.target.value;

    setSelectedOption(option);
    props.updateData(option);
  };
  return (
    <div>
      <h1>Choose Your Template</h1>

      <label>
        <input
          type="radio"
          value="Classic Version"
          checked={selectedOption === "Classic Version"}
          onChange={handleChange}
        ></input>
        <img src="/Assets/WashU/BCOptions.jpg" />
      </label>
      <label>
        <input
          type="radio"
          value="Updated Version"
          checked={selectedOption === "Classic Version"}
          onChange={handleChange}
        ></input>
        <img src="/Assets/WashU/BCOptions2.jpg" />
      </label>
    </div>
  );
};
export default BcOpt;
