import { useState } from "react";
const quantityOptions =[
  {value: '500', option: '500'},
  {value: '1000', option: '1000'},
  {value: '1500', option: '1500'},
  {value: 'other', option: 'Other'},
]
const inkOptions = [
  {value: 'color', option: 'Color'},
  {value: 'black', option: 'Black'},
]
const logoTypeOptions = [
  {value:'chancelorsLogo' , option:"Chancelor's Logo"},
  {value:'artsAndSciences' , option:"Art's & Sciences"},
  {value:'samFoxSchoolofDesignAndVisualArts' , option:"Sam Fox School of Design & Visual Arts"},
  {value:'other' , option: "Other"},
]
const PrintOpt = (props) => {
  const [quantity, setQuantity] = useState("");
  const [enteredQuantity, setEnteredQuantity] = useState("");
  const [inkOption, setInkOption] = useState("");
  const [logoType, setLogoType] = useState("");
  const [enteredLogoType, setEnteredLogoType] = useState("");
  const quantityChangleHandler = (event) => {
    event.preventDefault();
    let quantity = event.target.value;
    setQuantity(quantity);
    props.update(quantity);
  };
  const handleCustomQuantity = (event) => {
    event.preventDefault();
    let quantity = event.target.value;
    setEnteredQuantity(quantity);
    props.update(quantity);
  };
  const inkOptionChangeHandler = (event) => {
    event.preventDefault();
    let ink = event.target.value;
    setInkOption(ink);
    props.update(ink);
  };
  const logoTypeChangeHandler = (event) => {
    event.preventDefault();
    let logo = event.target.value;
    setLogoType(logo);
    props.update(logo);
  };
  const enteredLogoTypeChangeHandler = (event) => {
    event.preventDefault();
    let other = event.target.value;
    setEnteredLogoType(other);
    props.update(other);
  };


  return (

    <div>
      <h1>Printing Options</h1>
      <label>

        <input type="radio" options={quantityOptions} prepend="Quantity" handleChange={quantityChangleHandler} required={true} />
        {quantity === "other" && <input type ="text" label="Other Quantity" width="100px" placeholder="" required={quantity === "other"} handleChange={enteredQuantity} />}
      </label>
      <label>
      <input type="radio" options={inkOptions} prepend="Quantity" handleChange={inkOptionChangeHandler} required={true} />
      </label>
      <label>
        Logo Options
        <input type="radio" options={logoTypeOptions} prepend="Quantity" handleChange={logoTypeChangeHandler} required={true} />
        {logoType === "other" && <input type ="text" label="Other Logo Type" width="100px" placeholder="" required={logoType=== "other"} handleChange={enteredLogoTypeChangeHandler} />}
      </label>
    </div>
  );
};

export default PrintOpt;
