import { useState } from "react";

const PrintOpt = (props) =>{
    const [quantity, setQuantity] = useState('');
    const [inkOption, setInkOption] = useState('');


    const quantityChangleHandler = event =>{
        event.prevent.default();
        let quantity = event.target.value;
        setQuantity(quantity);
        props.updateData(quantity);
    }
    const inkOptionChangeHandler = event =>{
        event.prevent.default();
        let ink = event.target.value;
        setInkOption(ink);
        props.updateData(ink);
    }

    return <div><h1>Printing Options</h1>
  <label>Quantity
<input type ='radio' value ="500" checked ={quantity === '500'} onChange ={quantityChangleHandler}>500</input>
<input type ='radio' value ="1000" checked ={quantity === '1000'} onChange ={quantityChangleHandler}>1000</input>
<input type ='radio' value ="1500" checked ={quantity === '1500'} onChange ={quantityChangleHandler}>1500</input>
</label>
<label>Ink Options
<input type ='radio' value = 'Color' checked = {inkOption === 'Color'} onChange ={inkOptionChangeHandler}>Color</input>
<input type ='radio' value = 'Black' checked = {inkOption === 'Black'} onChange ={inkOptionChangeHandler}>Black</input>

</label>
    </div>
    }

    export default PrintOpt;
